INSERT INTO `book` (`id`, `book_title`, `book_description`, `language`, `book_cover`, `book_posted_time`, `book_number`, `uniqid`) VALUES
(5, 'Kader İle Seçim Arasındaki Savaş', '&lt;p class=&quot;p1&quot;&gt; &lt;/p&gt; &lt;br&gt;&lt;p class=&quot;p2&quot;&gt;Nereden geldik? Nereye gidiyoruz? Neden dünyamız bu kadar tehlikeli? Her zaman böyle miydi? Bir gün daha iyi olacak mı? İnsanın yaratılışından beri bu sorular insanlığın deneyiminin merkezinde yer almıştır. Din önderleri, filozof', 'turkish', 'upload/photos/2020/12/GtmLdRHaLrtuTAVveIfp_23_d14a9cdc9304eeb96cab63d747f46eed_image.jpg', '1608682998', 1, 'rseengxk'),
(6, 'Kölelikten Özgürlüğe', '&lt;div class=&quot;page&quot; title=&quot;Page 192&quot;&gt; <br>&lt;div class=&quot;section&quot;&gt; <br>&lt;div class=&quot;layoutArea&quot;&gt; <br>&lt;div class=&quot;column&quot;&gt; <br>&lt;p&gt;Hiç kimse size bir şeyin köleleri olduğumuzu söyledi mi? Bazı insanlar işe, paraya, ilişkilere ya da sporlara köledir. Bazıları ise alkole,', 'turkish', 'upload/photos/2020/12/UPEaKeO6wHC9Qtlr4FP5_23_c98383f99ea2c7692de059686aeaa585_image.jpg', '1604659551', 2, 'vowrdxws'),
(7, 'Umut Geliyor', '&lt;div class=&quot;page&quot; title=&quot;Page 230&quot;&gt; <br>&lt;div class=&quot;section&quot;&gt; <br>&lt;div class=&quot;layoutArea&quot;&gt; <br>&lt;div class=&quot;column&quot;&gt; <br>&lt;p&gt;Tarih büyük ölçüde ulusların yükseliş ve düşüşleriyle ilgilidir. İnsanlar gelir, insanlar gider. Barış zamanlarında yaşar ve savaş z', 'turkish', 'upload/photos/2020/12/WUaWoTi1ABR5rxVN6u4N_23_0b03fb6dec7da3d4583e827989e2c89c_image.jpg', '1604664798', 3, 'jmevabsw'),
(8, 'The Battle Between Fate And Choice', '&lt;p&gt;Can we really understand the Creator better by studying the things that He has made? Let&amp;rsquo;s consider an illustrative story, as we will do in these lessons, to help shed some light on that subject.&lt;/p&gt;', 'english', 'upload/photos/2020/12/zLEm4MsfjrpPYL2v3jlE_23_53eb1b5373224a09b135e8e72afc97c5_image.jpg', '1608685483', 1, 'xazhywvo'),
(9, 'From Slavery To Freedom', '&lt;p&gt;If you were a prophet do you think you would be a better person? Do you think your salvation would be assured? Do you think that your life would be free of problems and fears? Would you be happier?&lt;/p&gt;', 'english', 'upload/photos/2020/12/JaAlpjZpzjPIAYAWGKk8_23_1e3467a985837db0bec490e663784108_image.jpg', '1608685548', 2, 'flpzhoay'),
(10, 'Hope Is Coming', '&lt;p&gt;What is a real man? Television shows most often portray a &amp;ldquo;real man&amp;rdquo; as one who can kick down doors and solve problems with a gun. Of course, he always gets the girl...But what about the wise man, the honest man, the intellectual man, the compassionate father, and the loving husband? Where do they fit into manhood?&lt;/p&gt;', 'english', 'upload/photos/2020/12/IptUAij6SZqP9BwcHU7a_23_23440f5b992b7f0ae844af7e2a9dedf4_image.jpg', '1608685670', 3, 'xfvrxkwi'),
(11, 'Victory On The Horizon', '&lt;p&gt;What do you think is the most anticipated moment of peoples&amp;rsquo; lives? Graduating from high school or university? Getting a &amp;ldquo;real&amp;rdquo; job? How about buying a new car or first home?&lt;/p&gt;', 'english', 'upload/photos/2020/12/IPoGkKMembaTr4WHpltR_23_4897b6f9c9b69a5e9574789c05184104_image.jpg', '1608685734', 4, 'ypzggyxm'),
(12, 'Overcoming Death', '&lt;p&gt;How can a person purge the stain of shame that comes from im- morality and adultery? There are over 4000 &amp;ldquo;honor&amp;rdquo; killings each year in Turkey.&lt;/p&gt;', 'english', 'upload/photos/2020/12/U6efSrNKI4LybfKEqXhH_23_e457e915f4245714a506eab9375260b1_image.jpg', '1608685798', 5, 'epwsfput'),
(13, 'Kapıdaki Zafer', '&lt;p&gt;Her şeyi birbirine bağlayan bir iplik gibi zamanın i&amp;ccedil;inden ge&amp;ccedil;en bir vaat var. İnsanlık ilk g&amp;uuml;nah işlendikten sonraki yıllar i&amp;ccedil;inde bu vaadin yer ine gelmesini, yani Havva&amp;rsquo;nın soyunun yılanın başını ezmesini bekledi. Ancak o gelmedi, onlar da beklemeye devam etti. Ondan sonra, Allah İbrahim&amp;rsquo;e seslendi ve onun soyu aracılığıyla t&amp;uuml;m d&amp;uuml;nyayı bereketleyeceğini s&amp;ouml;yleyerek aynı vaadi yeniledi&lt;/p&gt;', 'turkish', 'upload/photos/2020/12/Fsm4XiRZ8OrYwAxWAVOd_26_a527cd65f6ea39d8d772604f86c8f790_image.jpg', '1608981338', 4, 'vjjxjfrm'),
(17, 'Ölüm Yenmek', '&lt;p&gt;İnsanlık tarihi boyunca pek &amp;ccedil;ok etkileyici erkek ve kadın yaşamıştır. Bazıları savaşlarda cesurca &amp;ccedil;arpışarak, diğerleriyse &amp;ouml;lerek &amp;uuml;n kazanmışlardır. Bazıları aletler, makineler ve yaşamı iyileştiren y&amp;ouml;ntemler icat ettikleri i&amp;ccedil;in meşhur olmuştur. Diğerleri c&amp;ouml;mertlikleriyle, derin d&amp;uuml;ş&amp;uuml;nceleriyle ve edebiyata katkılarıyla tanınır&lt;/p&gt;', 'turkish', 'upload/photos/2020/12/axkh8v1CIsz9kDfH45ni_26_0008e874d9a76e73ec6d3c919068cd4e_image.jpg', '1608981599', 5, 'nljyoxsb');
